/* eslint-disable react-native/no-inline-styles */
/* eslint-disable no-undef */
import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ScrollView } from 'react-native';
import storage from '@react-native-community/async-storage';

import Icon from 'react-native-vector-icons/MaterialIcons';
import Background from '../../../components/Background';
import * as CartActions from '../../../store/modules/cart/actions';
import { unformatNumber, unformatPrice } from '../../../util/format';
import api from '../../../services/api';

import {
  ConfirmButton,
  Card,
  CardHeader,
  CardTitle,
  Title,
  Subtitle,
  DateRow,
  DateContainer,
  TitleTotal,
} from './styles';
import {
  Container,
  Header,
  Text,
  Left,
  Body,
  Thumbnail,
  List,
  ListItem,
  Button,
  Footer,
  FooterTab,
} from 'native-base';

export default function OrderConfirmation({ navigation, route }) {
  const [loading, setLoading] = useState(false);
  const [idloja, setIdloja] = useState([]);
  const [address, setAdresses] = useState([]);
  const { orderDetails, paymentMethod } = route.params;

  const userId = useSelector(state => state.user.profile.id);
  const name = useSelector(state => state.user.profile.name);

  const products = useSelector(state =>
    state.cart.map(product => ({
      product_id: product.id,
      quantity: product.amount,
      price: product.price,
      total: product.amount * product.price,
    })),
  );

  useEffect(() => {
    async function BuscarEndereco() {
      try {
        const response = JSON.parse(
          await storage.getItem('KEY_VALUE_ADRESS_ENTREGA'),
        );

        setAdresses(response[0]);
      } catch (error) {
        Alert.alert('Error:', error.message);
      }
    }

    BuscarEndereco();
  }, [setAdresses]);

  useEffect(() => {
    async function loadId() {
      try {
        const response = await storage.getItem('KEY_VALUE_ID_ESTABELECIMENTO');

        setIdloja(response);
      } catch (err) {
        if (err.response) {
          console.tron.log('Erro no servidor');
        } else {
          console.tron.log('Falha ao conectar com o servidor');
        }
      }
    }

    loadId();
  }, []);
  const deleteidEstabelecimento = async () => {
    try {
      await storage.removeItem('KEY_VALUE_ID_ESTABELECIMENTO');
    } catch (error) {
      console.log(error.message);
    }
  };
  console.tron.log(address);
  const dispatch = useDispatch();
  async function handleSubmit() {
    try {
      setLoading(true);
      if (paymentMethod === 'CARTAO') {
        await api.post('orders', {
          user_id: userId,
          estabelecimento_id: idloja,
          status: 'PENDENTE',
          addressee: name,
          ship_postal_code: unformatNumber(address.postal_code),
          ship_street: address.street,
          ship_street_n: address.street_n,
          ship_neighborhood: address.neighborhood,
          ship_city: address.city,
          ship_state: address.state,
          ship_complement: address.complement,
          ship_reference: address.reference,
          payment_method: paymentMethod,
          payment_condition: 1,
          delivery_fee: unformatPrice(orderDetails.deliveryFee),
          products,
        });
      } else if (paymentMethod === 'DINHEIRO') {
        await api.post('orders', {
          user_id: userId,
          estabelecimento_id: idloja,
          status: 'PENDENTE',
          addressee: name,
          ship_postal_code: unformatNumber(address.postal_code),
          ship_street: address.street,
          ship_street_n: address.street_n,
          ship_neighborhood: address.neighborhood,
          ship_city: address.city,
          ship_state: address.state,
          ship_complement: address.complement,
          ship_reference: address.reference,
          payment_method: paymentMethod,
          payment_condition: 1,
          delivery_fee: unformatPrice(orderDetails.deliveryFee),
          products,
        });
      }
      setLoading(false);
      navigation.navigate('PaymentResult', { status: 'success' });
    } catch (err) {
      setLoading(false);
      navigation.navigate('PaymentResult', { status: 'failed' });
    }
  }

  return (
    <Background>
      <ScrollView>
        <Container>
          <Card>
            <List>
              <ListItem avatar>
                <Left>
                  <Thumbnail
                    large
                    source={{
                      uri:
                        'data:image/jpeg;base64,',
                    }}
                  />
                </Left>

                <Text>Supemercado Lima</Text>
              </ListItem>
            </List>
          </Card>
          <Card>
            <CardHeader>
              <Icon size={25} name="local-shipping" color="#F4A460" />
              <CardTitle>Produtos</CardTitle>
            </CardHeader>

            <DateRow>
              <DateContainer>
                <Title>jean</Title>
              </DateContainer>

              <DateContainer>
                <Title>{orderDetails.subtotal}</Title>
                <Title>{orderDetails.deliveryFee}</Title>
                <TitleTotal>{orderDetails.total}</TitleTotal>
              </DateContainer>
            </DateRow>
            <CardHeader>
              <Icon size={25} name="local-shipping" color="#F4A460" />
              <CardTitle>Entrega</CardTitle>
            </CardHeader>

            <Title>Destinatário</Title>
            <Subtitle>{`${address.street}, ${address.street_n} \n${
              address.neighborhood
            }\n${address.complement}\n${address.reference}\n${
              address.postal_code
            } - ${address.city} - ${address.state}`}</Subtitle>

            <CardHeader>
              <Icon size={25} name="local-shipping" color="#F4A460" />
              <CardTitle>Pagamento</CardTitle>
            </CardHeader>

            <DateRow>
              <DateContainer>
                <TitleTotal>{paymentMethod}</TitleTotal>
              </DateContainer>
            </DateRow>

            <CardHeader>
              <Icon size={25} name="local-shipping" color="#F4A460" />
              <CardTitle>Detalhes do pedido</CardTitle>
            </CardHeader>

            <DateRow>
              <DateContainer>
                <Title>Subtotal</Title>
                <Title>Taxa de entrega</Title>
                <Title>Total</Title>
              </DateContainer>

              <DateContainer>
                <Title>{orderDetails.subtotal}</Title>
                <Title>{orderDetails.deliveryFee}</Title>
                <TitleTotal>{orderDetails.total}</TitleTotal>
              </DateContainer>
            </DateRow>
          </Card>
        </Container>
      </ScrollView>
      <Footer>
        <FooterTab style={{ backgroundColor: '#F4A460' }}>
          <Button
            onPress={() => {
              handleSubmit();
              dispatch(CartActions.EsvaziarCart());
              deleteidEstabelecimento;
            }}
            loading={loading}>
            <Text style={{ fontSize: 15, color: '#fff' }}>
              Comfirmar Pedido
            </Text>
          </Button>
        </FooterTab>
      </Footer>
    </Background>
  );
}
OrderConfirmation.navigationOptions = ({ navigation }) => ({
  title: 'Comfirme seu pedido',
});
